// ì¹´í…Œê³ ë¦¬ ë²„íŠ¼ ìƒì„±
function createCategoryButtons() {
    const categories = Object.keys(emojiCategories);
    
    categories.forEach((categoryKey, index) => {
        const category = emojiCategories[categoryKey];
        const listItem = document.createElement('li');
        
        const button = document.createElement('button');
        button.className = 'btn btn-preset font-style-btn';
        if (index === 0) button.classList.add('active');
        button.dataset.category = categoryKey;
        button.textContent = category.name;
        button.setAttribute('aria-label', `${category.name} ì¹´í…Œê³ ë¦¬ ì„ íƒ`);
        button.setAttribute('aria-pressed', index === 0 ? 'true' : 'false');
        
        button.addEventListener('click', () => {
            // í™œì„±í™” ìƒíƒœ ì—…ë°ì´íŠ¸
            document.querySelectorAll('.font-style-btn').forEach(btn => {
                btn.classList.remove('active');
                btn.setAttribute('aria-pressed', 'false');
            });
            button.classList.add('active');
            button.setAttribute('aria-pressed', 'true');
            
            currentCategory = categoryKey;
            searchInput.value = '';
            displayEmojis(emojiCategories[categoryKey].emojis);
        });
        
        listItem.appendChild(button);
        categoryGrid.appendChild(listItem);
    });
}

// ì´ëª¨ì§€ í‘œì‹œ
function displayEmojis(emojis) {
    allEmojis = emojis;
    emojiGrid.innerHTML = '';
    emojiCount.textContent = `${emojis.length}ê°œ`;
    
    emojis.forEach(emoji => {
        const listItem = document.createElement('li');
        
        const emojiItem = document.createElement('div');
        emojiItem.className = 'emoji-item';
        emojiItem.textContent = emoji;
        emojiItem.setAttribute('role', 'button');
        emojiItem.setAttribute('tabindex', '0');
        emojiItem.setAttribute('aria-label', `ì´ëª¨ì§€ ${emoji} í´ë¦­í•˜ì—¬ ë³µì‚¬`);
        emojiItem.title = emoji;
        
        emojiItem.addEventListener('click', () => {
            copyEmoji(emoji);
        });
        
        emojiItem.addEventListener('keydown', (e) => {
            if (e.key === 'Enter' || e.key === ' ') {
                e.preventDefault();
                copyEmoji(emoji);
            }
        });
        
        listItem.appendChild(emojiItem);
        emojiGrid.appendChild(listItem);
    });
}

// ì´ëª¨ì§€ ë³µì‚¬
function copyEmoji(emoji) {
    navigator.clipboard.writeText(emoji).then(() => {
        showCopyToast(`"${emoji}" ë³µì‚¬ë˜ì—ˆìŠµë‹ˆë‹¤!`);
    }).catch(err => {
        console.error('ë³µì‚¬ ì‹¤íŒ¨:', err);
        showCopyToast('ë³µì‚¬ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.');
    });
}

// ê²€ìƒ‰ ì²˜ë¦¬
function handleSearch() {
    const searchTerm = searchInput.value.toLowerCase().trim();
    
    if (!searchTerm) {
        displayEmojis(emojiCategories[currentCategory].emojis);
        return;
    }
    
    // ê°„ë‹¨í•œ ê²€ìƒ‰ (ì´ëª¨ì§€ ìžì²´ì—ì„œ ê²€ìƒ‰)
    const filtered = allEmojis.filter(emoji => {
        return emoji.includes(searchTerm) || searchTerm.includes(emoji);
    });
    
    displayEmojis(filtered);
}

// ë³µì‚¬ í† ìŠ¤íŠ¸ í‘œì‹œ
function showCopyToast(message) {
    copyToastText.textContent = message;
    copyToast.style.display = 'flex';
    
    setTimeout(() => {
        copyToast.style.opacity = '0';
        setTimeout(() => {
            copyToast.style.display = 'none';
            copyToast.style.opacity = '1';
        }, 300);
    }, 2000);
}

